data.cssContent:
.legend-block{
    display: flex;
    flex-direction: column;
    max-width: 1600px;
    text-align: center;
}

.legend{
    text-align: left;
    position: relative;
    font-size: 12px;
    color: #333333;
    margin-bottom: 18px;
}

.legend:last-child {
    margin-bottom: 0px;
}

@media (min-width: 768px) {
    .legend-block{
        flex-direction: row;
        flex-wrap: wrap;
        justify-content: space-between;
    }

    .legend{
        flex: 0 42%;
    }
}

@media (min-width: 1200px){
    .legend{
        font-size: 14px;
        flex: 0 32%;
    }

    .legend:nth-child(2n+1) {
        margin-bottom: 25px
    }
}

.collapse-content{
    padding: 0px;
    margin: 0px 0px 30px;
    color: #333333;
    font-size: 12px;
}

.collapse-content span {
    font-weight: bold;
    display: inline-block;
    margin-bottom: 5px;
}

.collapse-content p {
    line-height: 1.67;
    padding: 0px;
    margin: 0px;
    font-family: Roboto;
}

.collapse-content:last-child {
    margin: 0px;
}

@media (min-width: 1200px){
    .collapse-content{
        font-size: 14px;
    }

    .collapse-content p {
        line-height: 1.43;
    }
}
